CREATE DATABASE web_cosmetic_nhom10;

use web_cosmetic_nhom10;

create table user(
                     id bigint not null primary key auto_increment,
                     username VARCHAR(255),
                     fullname VARCHAR(255),
                     password VARCHAR(255),
                     email VARCHAR(255),
                     phone VARCHAR(15),
                     country VARCHAR(50),
                     roleid BIGINT DEFAULT 1,
                     avatar VARCHAR(255),
                     created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
                     updated_at DATETIME DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP
);

alter table user change avatar avatar varchar(255) default 'default-avatar.jpg';

create table role(
                     id bigint not null primary key auto_increment,
                     name varchar(255),
                     code varchar(255)
);

alter table user add constraint fk_user_role foreign key (roleid) references role(id);

create table product(
                        id bigint not null primary key auto_increment,
                        name VARCHAR(255),
                        thumbnailImg text,
                        price float,
                        discount float,
                        sold int,
                        shortDescription text,
                        longDesscription text,
                        rate float,
                        weight varchar(20),
                        dimension varchar(20),
                        originid bigint,
                        categoryid bigint,
                        companyid bigint,
                        tagid bigint
);

alter table product change longDesscription longDescription text;
alter table product add created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP;
alter table product add updated_at DATETIME DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP;
alter table product change categoryid categoryid bigint default 1;

create table productimages (
                               id bigint not null primary key auto_increment,
                               link text,
                               productid bigint
);

alter table productimages add constraint fk_productimages_product foreign key (productid) references product(id);

create table inventory (
                           id bigint not null primary key auto_increment,
                           productid bigint,
                           amount int
);

alter table inventory add constraint fk_inventory_product foreign key (productid) references product(id);

create table cart(
                     id bigint not null primary key auto_increment,
                     total double null,
                     userid bigint not null
);

alter table cart add constraint fk_cart_user foreign key (userid) references user(id);

create table cartitems (
                           cartid bigint not null,
                           productid bigint not null,
                           quantity int not null,
                           primary key (cartid, productid)
);

alter table cartitems add constraint fk_cartitems_cart foreign key (cartid) references cart(id);
alter table cartitems add constraint fk_cartitems_product foreign key (productid) references product(id);

create table orderstatus (
                             id bigint not null primary key auto_increment,
                             name text
);

create table orders (
                        id bigint not null primary key auto_increment,
                        userid bigint,
                        statusid bigint,
                        address text,
                        created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
                        updated_at DATETIME DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP
);

alter table orders add constraint fk_orders_orderstatus foreign key (statusid) references orderstatus(id);

create table orderdetails (
                              id bigint not null primary key auto_increment,
                              orderid bigint,
                              productid bigint,
                              quantity int,
                              price float
);

alter table orderdetails add constraint fk_orderdetails_order foreign key (orderid) references orders(id);
alter table orderdetails add constraint fk_orderdetails_product foreign key (productid) references product(id);

create table company (
                         id bigint not null primary key auto_increment,
                         name varchar(255)
);

alter table product add constraint fk_product_company foreign key (companyid) references company(id);

create table tag (
                     id bigint not null primary key auto_increment,
                     name varchar(255)
);

alter table product add constraint fk_product_tag foreign key (tagid) references tag(id);

create table origin (
                        id bigint not null primary key auto_increment,
                        name varchar(255)
);

alter table product add constraint fk_product_origin foreign key (originid) references origin(id);

create table category (
                          id bigint not null primary key auto_increment,
                          name varchar(255)
);

alter table product add constraint fk_product_category foreign key (categoryid) references category(id);

create table review (
                        id bigint not null primary key auto_increment,
                        productid bigint,
                        userid bigint,
                        comment text,
                        created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
                        updated_at DATETIME DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP
);

alter table review add constraint fk_review_product foreign key (productid) references product(id);
alter table review add constraint fk_review_user foreign key (userid) references user(id);

create table contact (
                         id bigint not null primary key auto_increment,
                         name varchar(255),
                         address text,
                         email varchar(255),
                         phone varchar(255)
);

create table aboutus (
                         id bigint not null primary key auto_increment,
                         name varchar(255),
                         description text,
                         img text,
                         socialmedia text
);










