INSERT INTO `product` (`id`, `name`, `thumbnailImg`, `price`, `discount`, `sold`, `shortDescription`, `longDescription`, `rate`, `weight`, `dimension`, `originid`, `categoryid`, `companyid`, `tagid`, `created_at`, `updated_at`) VALUES (37, 'Paula\'s Choice RESIST Perfectly Balanced Foaming Cleanser', 'Paula\'sChoiceRESISTPerfectlyBalancedFoamingCleanser.jpg', '33.22', '23.254', 356, 'A pearlescent foam that whisks away impurities without stripping skin.', 'PERFECTLY BALANCED SKIN: This balancing face wash helps visibly reduce enlarged pores & leaves skin with a refreshed feeling. Replenishing ingredients such as hyaluronic acid help condition skin leaving your face feeling exceptionally smooth & clear. PACKAGING MAY VARY.\r\nPaula\'s Choice RESIST Perfectly Balanced Anti-Aging Facial Cleanser is formulated to gently remove makeup and excess oil to leave the face washed clean but never tight or dry. Also contains sunflower seed oil & aloe to keep skin supple & youthful.', '4.5', '190', '5x6x17', 3, 4, 6, 3, '2022-06-15 16:00:35', '2022-06-19 20:43:34');
