INSERT INTO `product` (`id`, `name`, `thumbnailImg`, `price`, `discount`, `sold`, `shortDescription`, `longDescription`, `rate`, `weight`, `dimension`, `originid`, `categoryid`, `companyid`, `tagid`, `created_at`, `updated_at`) VALUES (40, 'Paula\'s Choice The UnScrub Cleansing Gentle Face Scrub', 'Paula\'sChoiceTheUnScrubCleansingGentleFaceScrub.jpg', 29, '20.3', 155, 'This one-of-a-kind, gentle cleansing face scrub whisks away makeup, sunscreen and impurities while renewing skin with round, biodegradable jojoba beads.', 'MILKY-GEL EXFOLIATING CLEANSER: The UnScrub removes makeup, sunscreen, and impurities leaving skin feeling ultra-clean, soft and healthy-looking. Thoroughly cleanses and gently exfoliates without disrupting skin\'s barrier.\r\nPaula\'s Choice UnScrub cleanses skin with environmentally friendly, biodegradable jojoba beads that dissolve as you cleanse, making it impossible to overscrub. With skin-soothing chamomile & green tea, plus naturally exfoliating Soapberry Peel extract.', '4.5', '118', '4x3x14', 4, 4, 6, 3, '2022-06-15 16:00:35', '2022-06-19 20:43:34');
