INSERT INTO `product` (`id`, `name`, `thumbnailImg`, `price`, `discount`, `sold`, `shortDescription`, `longDescription`, `rate`, `weight`, `dimension`, `originid`, `categoryid`, `companyid`, `tagid`, `created_at`, `updated_at`) VALUES (42, 'Paula\'s Choice SKIN BALANCING Oil-Reducing Cleanser', 'Paula\'sChoiceSKINBALANCINGOilReducingCleanser.jpg', 19, '11.4', 386, 'Blance hydration & mattify shine in one gentle, effective step', 'CONTROL OIL WITHOUT DRYING SKIN: This concentrated cream-to-foam cleanser works quickly to thoroughly rinse away oil, buildup, sunscreen & makeup. It helps keep pores clear of congestion-causing debris to visibly reduce clogged & enlarged pores while helping control excess shine & oil.\r\nPaula\'s Choice SKIN BALANCING Oil-Reducing Cleanser whisks away the dirt, debris, and impurities from the day, restoring balance & refreshing skin. Skin feels perfectly prepped and refined - never tight or uncomfortable after cleansing.', '4.6', '237', '5x5x16', 3, 4, 2, 1, '2022-06-15 16:00:35', '2022-06-19 20:43:34');
