INSERT INTO `product` (`id`, `name`, `thumbnailImg`, `price`, `discount`, `sold`, `shortDescription`, `longDescription`, `rate`, `weight`, `dimension`, `originid`, `categoryid`, `companyid`, `tagid`, `created_at`, `updated_at`) VALUES (49, 'Paula\'s Choice HYDRALIGHT Gentle Gel Cleanser', 'Paula\'sChoiceHYDRALIGHTGentleGelCleanser.jpg', '30.55', '27.495', 99, 'This antioxidant-enriched formula provides gentle, pure mineral broad-spectrum sun protection to defend skin from premature aging.', 'ULTRA-GENTLE & EFFECTIVE CLEANSING GEL: This uniquely-formulated face wash quickly & effectively rinses skin clean, removing excess oil, skin-dulling impurities, sunscreen and makeup - without stripping or irritating skin. Works wonders for those with an oily t-zone because it doesn\'t doesn\'t leave skin feeling dry.\r\nPaula\'s Choice HYDRALIGHT Face Cleanser is an exceptionally gentle gel cleanser that instantly leaves skin feeling refreshed and looking more refined.', '4.4', '237', '19x6x6s', 7, 4, 3, 1, '2022-06-15 16:00:35', '2022-06-19 20:43:34');
INSERT INTO `product` (`id`, `name`, `thumbnailImg`, `price`, `discount`, `sold`, `shortDescription`, `longDescription`, `rate`, `weight`, `dimension`, `originid`, `categoryid`, `companyid`, `tagid`, `created_at`, `updated_at`) VALUES (50, 'Paula\'s Choice RESIST Super-Light Daily Wrinkle Defense SPF 30', 'Paula\'sChoiceRESISTSuperLightDailyWrinkleDefenseSPF30.jpg', '28.93', '26.037', 57, 'Matte Tinted Face Moisturizer with UVA & UVB Protection, Anti-Aging Mineral Sunscreen for Oily Skin, Fragrance-Free & Paraben-Free, 2 Ounce', 'MULTI-PURPOSE FORMULA HYDRATES, FIRMS, PROTECTS: This unique formula contains lightly tinted moisturizer with sunscreen and nourishing antioxidants to protect your skin from environmental and sun damage. Evens out skin tone; silky matte finish.\r\nPaula\'s Choice RESIST Super-Light Daily Wrinkle Defense SPF 30 Tinted Matte Moisturizer with Antioxidants & Resveratrol is formulated to repair & renew sun damaged skin while preventing further damage, minus the white cast associated with most sunscreens.\r\nFormulated for normal to oily and combination skin this moisturizer with 30 SPF protection visibly reduces fine lines and wrinkles, evens out skin tone and with daily use you\'ll see firmer skin. Works equally beautifully as a base for makeup or worn bare.', '3.7', '60', '10x5x8', 5, 5, 5, 1, '2022-06-15 16:00:35', '2022-06-19 20:43:34');
INSERT INTO `product` (`id`, `name`, `thumbnailImg`, `price`, `discount`, `sold`, `shortDescription`, `longDescription`, `rate`, `weight`, `dimension`, `originid`, `categoryid`, `companyid`, `tagid`, `created_at`, `updated_at`) VALUES (51, 'Paula\'s Choice RESIST Daily Hydrating Fluid Face Moisturizer SPF 50', 'Paula\'sChoiceRESISTDailyHydratingFluidFaceMoisturizerSPF50.jpg', '36.18', '28.944', 795, 'Daily Hydrating Fluid Face Moisturizer SPF 50, UVA & UVB Protection, Chamomile & Vitamin E, Sunscreen for Oily Skin', 'RESIST AND RENEW: This light yet powerful SPF 50 moisturizer provides broad-spectrum sun protection to prevent premature aging of the skin and boosts your skin\'s defenses with a blend of potent antioxidants. Has a water-light consistency & matte-finish. PACKAGING MAY VARY.\r\nPaula\'s Choice RESIST Youth-Extending Daily Hydrating Fluid SPF 50 Anti-Aging Face Moisturizer provides effective UVA & UVB protection plus hydration for a healthier younger-looking complexion. Its satin-matte finish leaves skin feeling smooth & hydrated.', '4.5', '60', '10x5x8', 6, 5, 6, 2, '2022-06-15 16:00:35', '2022-06-19 20:43:34');
INSERT INTO `product` (`id`, `name`, `thumbnailImg`, `price`, `discount`, `sold`, `shortDescription`, `longDescription`, `rate`, `weight`, `dimension`, `originid`, `categoryid`, `companyid`, `tagid`, `created_at`, `updated_at`) VALUES (52, 'Paula\'s Choice CLEAR Ultra-Light Hydrating Oil Free Moisturizer SPF 30', 'Paula\'sChoiceCLEARUltraLightHydratingOilFreeMoisturizerSPF30.jpg', '28.89', '20.223', 391, 'CLEAR Ultra-Light Hydrating Oil Free Moisturizer SPF 30, UVA UVB Protection, Pore Minimizer Sunscreen for Face', 'MATTE-FINISH BROAD SPECTRUM SUNSCREEN: Wave goodbye to greasy sunscreen! Our lightweight, oil-free moisturizer with SPF 30+ protects your skin from UVA & UVB damage while enriching skin with antioxidants to promote a healthy, even-toned look & feel.\r\nPaula\'s Choice CLEAR Ultra-Light Daily Hydrating Fluid SPF 30+ is a daytime, sun-protecting fluid that softens the look of enlarged pores, helps control excess shine, and defends skin against damage caused by UVA & UVB rays. Plant-derived, skin-soothing antioxidants along with weightless hydrators help smooth and calm skin to combat dry, flaky patches.\r\nCLEAR Ultra-Light Daily Fluid SPF 30+ is ideal for all skin types, though its robust formula is uniquely-suited to best meet the needs of those with enlarged pores & blemishes and combination to oily skin. Apply liberally as the last step in your morning skin care routine.', '4.5', '60', '10x5x8', 3, 5, 6, 2, '2022-06-15 16:00:35', '2022-06-19 20:43:34');
INSERT INTO `product` (`id`, `name`, `thumbnailImg`, `price`, `discount`, `sold`, `shortDescription`, `longDescription`, `rate`, `weight`, `dimension`, `originid`, `categoryid`, `companyid`, `tagid`, `created_at`, `updated_at`) VALUES (53, 'Paula\'s Choice Extra Care Non Greasy', 'Paula\'sChoiceExtraCareNonGreasy.jpg', 30, 24, 1616, 'Oil Free Face & Body Sunscreen SPF 50, UVA & UVB Protection, Water & Sweat Resistant', 'MATTE-FINISH BROAD SPECTRUM SUN PROTECTION: Wave goodbye to greasy sunscreen. Our lightweight, oil-free formula is SPF 50 & protects your skin from damage from UVB rays & UVA rays while enriching skin with antioxidants to promote a youthful appearance. PACKAGING MAY VARY.\r\nPaula\'s Choice Extra Care Non-Greasy Sunscreen is water- and sweat-resistant; ideal for outdoor & water-based activities. This silky oil-free formula is great for all skin types including oily and combination skin. Goes on smooth & dries to a soft matte finish.\r\nNever thick or greasy; our SPF 50 sunscreen is suitable for the face and body. This formula\'s soft matte finish can be worn with a bare face or under makeup. Protect your skin from further sun and environmental damage with antioxidants, vitamins A, E and C, hydrating aloe and oil-absorbing kaolin.', 4, '148', '5x5x15', 2, 5, 5, 2, '2022-06-15 16:00:35', '2022-06-19 20:43:34');
INSERT INTO `product` (`id`, `name`, `thumbnailImg`, `price`, `discount`, `sold`, `shortDescription`, `longDescription`, `rate`, `weight`, `dimension`, `originid`, `categoryid`, `companyid`, `tagid`, `created_at`, `updated_at`) VALUES (54, 'Paula\'s Choice-DEFENSE Essential Glow Mineral Moisturizer', 'Paula\'sChoiceDEFENSEEssentialGlowMineralMoisturizer.jpg', '26.75', '18.725', 1073, ' SPF 30 w/Kiwi Extract, Vitamin C & E, Niacinamide, Argan Oil & Resveratrol, Anti-Pollution Protection for All Skin Types', 'RADIANCE-BOOSTING MOISTURIZER WITH SPF: This daytime anti-aging moisturizer combines mineral-based, UVA/UVB broad-spectrum sun protection with brightening vitamin C, potent antioxidants, and skin-soothing plant extracts to create an instant, healthy, hydrated glow for makeup-optional skin.\r\nPaula\'s Choice DEFENSE collection was created to address the specific needs of skin is exposed to environmental stressors such as smog, small particulate matter, car exhaust, cigarette smoke, and blue light from digital devices', '4.5', '60', '10x5x8', 1, 6, 3, 2, '2022-06-15 16:00:35', '2022-06-19 20:43:34');
INSERT INTO `product` (`id`, `name`, `thumbnailImg`, `price`, `discount`, `sold`, `shortDescription`, `longDescription`, `rate`, `weight`, `dimension`, `originid`, `categoryid`, `companyid`, `tagid`, `created_at`, `updated_at`) VALUES (55, 'Paula\'s Choice SKIN RECOVERY Daily Moisturizing Lotion', 'Paula\'sChoiceSKINRECOVERYDailyMoisturizingLotion.jpg', '29.85', '17.91', 328, 'SKIN RECOVERY Daily Moisturizing Lotion SPF 30 Mineral Sunscreen, 2 Ounce Bottle Moisturizing Sunscreen for Dry and Sensitive Facial Skin', 'RADIANT-FINISH BROAD SPECTRUM SUN PROTECTION--Hello hydrating sunscreen! Our daily moisturizing formula in SPF 30 protects your skin from damage from UVB rays & UVA rays while enriching skin with moisture & antioxidants to promote a youthful appearance\r\nPaula\'s Choice--SKIN RECOVERY Daily Moisturizing Lotion SPF 30 Mineral Sunscreen contains a unique complex of anti-aging peptides in a rich cream formula that provides skin-recovering ingredients to transform dry skin as it protects from aging sun damage\r\nUse Paula\'s Choice--SKIN RECOVERY Daily Moisturizing Lotion SPF 30 Mineral Sunscreen daily as part of your morning skin care, along with your favorite Paula\'s Choice cleanser, toner, serums, spot treatments, exfoliators, and eye creams as needed.', '4.4', '60', '10x5x8', 5, 6, 4, 3, '2022-06-15 16:00:35', '2022-06-19 20:43:34');
