INSERT INTO `product` (`id`, `name`, `thumbnailImg`, `price`, `discount`, `sold`, `shortDescription`, `longDescription`, `rate`, `weight`, `dimension`, `originid`, `categoryid`, `companyid`, `tagid`, `created_at`, `updated_at`) VALUES (62, 'Paula\'s Choice BOOST Hyaluronic Acid + Peptide Lip Booster', 'Paula\'sChoiceBOOSTHyaluronicAcidPeptideLipBooster.jpg', '26.8', '18.76', 174, 'Hyaluronic Acid + Peptide Lip Booster, Hydrating Treatment for Lip Volume, Loss of Firmness & Fine Lines, with Squalane, Fragrance-free & Paraben-free', 'VISIBLY BOOST LIP VOLUME: This dewy-finish treatment contains hyaluronic acid & konjac sponge spheres that boost lip volume by absorbing & expanding water to give lips a fuller look.\r\nFIRM & FIGHT FINE LINES: Targeted peptides help counteract thinning, support lip’s natural shape & encourage repair for a fuller look.\r\nSOFTEN & HYDRATE: Squalane & soy + cocoa & shea butters deliver an extra boost of antioxidants while preventing moisture loss to keep lips smooth, soft & plump.\r\nTHE RESULTS: In a consumer self-assessment of 72 people after 8 weeks of use, 82% agree lips feel smoother, 82% agree lips feel softer, 80% agree lips appear more hydrated.\r\nPaula\'s Choice Skincare makes science-backed products that work. No fragrance, no parabens, no fluff. Just effective, cruelty-free formulas that target any concern from wrinkles to breakouts.\r\n', '4.4', '10', '7x2x5', 3, 3, 1, 1, '2022-06-15 16:00:35', '2022-06-19 20:43:34');
INSERT INTO `product` (`id`, `name`, `thumbnailImg`, `price`, `discount`, `sold`, `shortDescription`, `longDescription`, `rate`, `weight`, `dimension`, `originid`, `categoryid`, `companyid`, `tagid`, `created_at`, `updated_at`) VALUES (63, 'Paula\'s Choice CLINICAL 20% Niacinamide Vitamin B3 Concentrated Serum', 'Paula\'sChoiceCLINICAL20NiacinamideVitaminB3ConcentratedSerum.jpg', 29, '20.3', 762, '20% Niacinamide Vitamin B3 Concentrated Serum, Anti-Aging Treatment for Discoloration and Minimizing Large Pores, Fragrance-Free & Paraben-Free, 0.67 Ounce Dropper Bottle', 'DIMINISH LARGE PORES: Tighten & refine the look of stretched, sagging pores caused by age or sun damage.\r\nIMPROVE SKIN TONE: Vitamin C & licorice root visibly brighten skin and improve blotchy tone, discolorations and uneven skin tone\r\nMINIMIZE HARD, CLOGGED BUMPS: Reduce bumps & clogged pores and even out rough, orange-peel texture for smoother, glowing skin.\r\nADVANCED 20% CONCENTRATION: Safe & effective concentration targets advanced signs of aging for a firmer, younger-looking complexion. Plus, it improves pore function while protecting against further pore damage.', '4.4', '20', '5x6x5', 2, 3, 2, 1, '2022-06-15 16:00:35', '2022-06-19 20:43:34');
INSERT INTO `product` (`id`, `name`, `thumbnailImg`, `price`, `discount`, `sold`, `shortDescription`, `longDescription`, `rate`, `weight`, `dimension`, `originid`, `categoryid`, `companyid`, `tagid`, `created_at`, `updated_at`) VALUES (64, 'Paula\'s Choice CLINICAL Discoloration Repair Serum', 'Paula\'sChoiceCLINICALDiscolorationRepairSerum.jpg', 30, 21, 621, 'Serum with Tranexemic Acid for Stubborn Dark Spots, Post-Acne Marks & Sun Damage, Paraben-Free & Fragrance-Free', 'VISIBLY FADE DARK SPOTS: Take on stubborn brown & gray patches & post-acne discoloration with clinical-strength brighteners that disrupt the causes of unwanted color changes.\r\nCUTTING-EDGE INGREDIENTS: 3% tranexamic acid, 5% niacinamide & 0.5% bakuchiol work together with pure tocopherol (vitamin E) & milk thistle to target & steadily improve the look of dark spots & discoloration through distinct pathways in skin.\r\nIMPROVE MASK-LIKE DISCOLORATION: This non-irritating treatment promotes an even skin tone while brightening & fading the look of stubborn, diffuse discolorations around the cheeks, forehead, nose & chin.', '4.2', '30', '8x3x6', 1, 3, 1, 1, '2022-06-15 16:00:35', '2022-06-19 20:43:34');
INSERT INTO `product` (`id`, `name`, `thumbnailImg`, `price`, `discount`, `sold`, `shortDescription`, `longDescription`, `rate`, `weight`, `dimension`, `originid`, `categoryid`, `companyid`, `tagid`, `created_at`, `updated_at`) VALUES (65, 'Paula\'s Choice CLEAR Regular Strength Skin Clearing Treatment', 'Paula\'sChoiceCLEARRegularStrengthSkinClearingTreatment.jpg', 25, 20, 2251, '2.5% Benzoyl Peroxide for Facial Acne, Redness Relief', 'Paula\'s Choice CLEAR Daily Skin Clearing Acne Treatment is uniquely-formulated to fight acne using 2.5% benzoyl peroxide while also hydrating & soothing skin with non-fragrant, nourishing plant extracts. This blemish-fighting solution works well for all skin types and it\'s even suitable for skin prone to cystic acne.\r\nFormulated with 2.5% benzoyl peroxide to help fight existing acne breakouts & prevent future blemishes on the face and body, this effective treatment delivers best results when used daily. Apply after cleansing and exfoliating, always finishing with an SPF 30 or greater for daytime use. Sets to an invisible matte finish & looks great under makeup', '4.4', '60', '10x5x8', 4, 6, 3, 3, '2022-06-15 16:00:35', '2022-06-19 20:43:34');
